import {Module} from '@nestjs/common';
import {TypeOrmModule} from '@nestjs/typeorm';
import {ConfigModule, ConfigService} from '@nestjs/config';
import {FacturasVentasModule} from './facturas-ventas/facturas-ventas.module';
import {OrdenesModule} from './ordenes/ordenes.module';
import {DomiciliosModule} from './domicilios/domicilios.module';
import {ClientesModule} from './clientes/clientes.module';
import {OrdenesProductosModule} from './ordenes-productos/ordenes-productos.module';
import {ProductosModule} from './productos/productos.module';
import {DomiciliariosModule} from './domiciliarios/domiciliarios.module';
import {FacturasPagosModule} from './facturas-pagos/facturas-pagos.module';
import {AuthModule} from './auth/auth.module';
import {PizzaSaboresModule} from './pizza-sabores/pizza-sabores.module';
import {ThrottlerModule} from '@nestjs/throttler';
import {BullModule} from '@nestjs/bullmq';
import {RedisModule} from './common/redis/redis.module';

@Module({
	imports: [
		ConfigModule.forRoot({
			isGlobal: true,
			envFilePath: '.env',
		}),
		TypeOrmModule.forRootAsync({
			imports: [ConfigModule],
			inject: [ConfigService],
			useFactory: (configService: ConfigService) => {
				const config = {
					type: 'postgres' as const,
					host: configService.get<string>('DATABASE_HOST', 'localhost'),
					port: Number(configService.get<string>('DATABASE_PORT', '5432')),
					username: configService.get<string>('DATABASE_USER', 'postgres'),
					password: configService.get<string>('DATABASE_PASSWORD', 'postgres'),
					database: configService.get<string>('DATABASE_NAME', 'postgres'),
					autoLoadEntities: true,
					synchronize: true,
				};
				console.log('TypeORM Config:', {...config, password: '***'});
				return config;
			},
		}),
		ThrottlerModule.forRoot([{
			ttl: 60000,
			limit: 100,
		}]),
		BullModule.forRootAsync({
			imports: [ConfigModule],
			inject: [ConfigService],
			useFactory: (configService: ConfigService) => ({
				connection: {
					host: configService.get<string>('REDIS_HOST', 'localhost'),
					port: Number(configService.get<string>('REDIS_PORT', '6379')),
				},
			}),
		}),
		RedisModule,
		FacturasVentasModule,
		OrdenesModule,
		DomiciliosModule,
		ClientesModule,
		OrdenesProductosModule,
		ProductosModule,
		DomiciliariosModule,
		FacturasPagosModule,
		AuthModule,
		PizzaSaboresModule,
	],
	controllers: [],
	providers: [],
})
export class AppModule {}

import {Body, Controller, Get, Post, Request, UseGuards} from '@nestjs/common';
import {ApiBearerAuth, ApiTags} from '@nestjs/swagger';
import {AuthService} from './auth.service';
import {LoginDto} from './dto/login.dto';
import {RegisterDto} from './dto/register.dto';
import {AuthResponseDto} from './dto/auth-response.dto';
import {RefreshTokenDto} from './dto/refresh-token.dto';
import {Public} from './decorators/public.decorator';
import {Roles} from './decorators/roles.decorator';
import {RolesGuard} from './guards/roles.guard';
import {JwtAuthGuard} from './guards/jwt-auth.guard';
import {Role} from './roles.enum';

@ApiTags('auth')
@Controller('auth')
export class AuthController {
	constructor(private readonly authService: AuthService) {}

	@Public()
	@Post('register')
	async register(@Body() dto: RegisterDto): Promise<AuthResponseDto> {
		return this.authService.register(dto);
	}

	@Public()
	@Post('login')
	async login(@Body() dto: LoginDto): Promise<AuthResponseDto> {
		return this.authService.login(dto);
	}

	@Public()
	@Post('refresh')
	async refresh(@Body() dto: RefreshTokenDto): Promise<AuthResponseDto> {
		return this.authService.refreshToken(dto.refreshToken);
	}

	@ApiBearerAuth()
	@UseGuards(JwtAuthGuard, RolesGuard)
	@Roles(Role.Admin, Role.Cocina, Role.Mesero)
	@Get('me')
	async me(@Request() req): Promise<AuthResponseDto> {
		return this.authService.toAuthResponse(req.user);
	}
}

import {BadRequestException, Injectable, UnauthorizedException} from '@nestjs/common';
import {InjectRepository} from '@nestjs/typeorm';
import {Repository} from 'typeorm';
import * as bcrypt from 'bcrypt';
import {JwtService} from '@nestjs/jwt';
import {ConfigService} from '@nestjs/config';
import {User} from './esquemas/user.entity';
import {LoginDto} from './dto/login.dto';
import {RegisterDto} from './dto/register.dto';
import {Role} from './roles.enum';
import {AuthResponseDto} from './dto/auth-response.dto';
import {JwtPayload} from './types/jwt-payload.type';

@Injectable()
export class AuthService {
	constructor(
		@InjectRepository(User) private readonly usersRepository: Repository<User>,
		private readonly jwtService: JwtService,
		private readonly configService: ConfigService,
	) {}

	async register(dto: RegisterDto): Promise<AuthResponseDto> {
		const existing = await this.usersRepository.findOne({where: {username: dto.username}});
		if (existing) {
			throw new BadRequestException('Username ya registrado');
		}

		const hash = await bcrypt.hash(dto.password, 10);
		const roles = dto.roles && dto.roles.length > 0 ? dto.roles : [Role.Mesero];

		const user = this.usersRepository.create({
			username: dto.username,
			name: dto.name,
			passwordHash: hash,
			roles,
		});
		const saved = await this.usersRepository.save(user);
		return this.toAuthResponse(saved);
	}

	async login(dto: LoginDto): Promise<AuthResponseDto> {
		const user = await this.usersRepository.findOne({
			where: {username: dto.username},
			select: ['id', 'username', 'name', 'passwordHash', 'roles', 'createdAt', 'updatedAt'],
		});
		if (!user) {
			throw new UnauthorizedException('Credenciales inv├ílidas');
		}

		const valid = await bcrypt.compare(dto.password, user.passwordHash);
		if (!valid) {
			throw new UnauthorizedException('Credenciales inv├ílidas');
		}

		return this.toAuthResponse(user);
	}

	async refreshToken(token: string): Promise<AuthResponseDto> {
		try {
			const payload = this.jwtService.verify(token);
			const user = await this.usersRepository.findOne({
				where: {id: payload.sub},
				select: ['id', 'username', 'name', 'passwordHash', 'roles', 'createdAt', 'updatedAt'],
			});
			if (!user) {
				throw new UnauthorizedException('Usuario no encontrado');
			}
			return this.toAuthResponse(user);
		} catch (error) {
			throw new UnauthorizedException('Refresh token inv├ílido o expirado');
		}
	}

	toAuthResponse(user: User): AuthResponseDto {
		const payload: JwtPayload = {
			sub: user.id,
			username: user.username,
			roles: user.roles,
		};
		const accessToken = this.jwtService.sign(payload);
		const refreshToken = this.jwtService.sign(payload, { expiresIn: '7d' });
		return {
			id: user.id,
			username: user.username,
			name: user.name,
			roles: user.roles,
			accessToken,
			refreshToken,
		};
	}
}

import { ApiProperty } from '@nestjs/swagger';
import { IsNotEmpty, IsString } from 'class-validator';

export class RefreshTokenDto {
	@ApiProperty()
	@IsString()
	@IsNotEmpty()
	refreshToken: string;
}

import {
	Column,
	CreateDateColumn,
	Entity,
	PrimaryGeneratedColumn,
	UpdateDateColumn,
} from 'typeorm';
import {Role} from '../roles.enum';

@Entity({name: 'users'})
export class User {
	@PrimaryGeneratedColumn('uuid')
	id: string;

	@Column({unique: true})
	username: string;

	@Column({nullable: true})
	name?: string;

	@Column({select: false})
	passwordHash: string;

	@Column({type: 'text', array: true, default: '{}'})
	roles: Role[];

	@CreateDateColumn()
	createdAt: Date;

	@UpdateDateColumn()
	updatedAt: Date;
}

import {CanActivate, ExecutionContext, Injectable} from '@nestjs/common';
import {Reflector} from '@nestjs/core';
import {ROLES_KEY} from '../decorators/roles.decorator';
import {IS_PUBLIC_KEY} from '../decorators/public.decorator';
import {Role} from '../roles.enum';

@Injectable()
export class RolesGuard implements CanActivate {
	constructor(private readonly reflector: Reflector) {}

	canActivate(context: ExecutionContext): boolean {
		const isPublic = this.reflector.getAllAndOverride<boolean>(IS_PUBLIC_KEY, [
			context.getHandler(),
			context.getClass(),
		]);
		if (isPublic) {
			return true;
		}

		const requiredRoles = this.reflector.getAllAndOverride<Role[]>(ROLES_KEY, [
			context.getHandler(),
			context.getClass(),
		]);
		if (!requiredRoles || requiredRoles.length === 0) {
			return true;
		}

		const {user} = context.switchToHttp().getRequest();
		if (!user || !user.roles) {
			return false;
		}

		return requiredRoles.some((role) => user.roles.includes(role));
	}
}

import {Injectable, UnauthorizedException} from '@nestjs/common';
import {ConfigService} from '@nestjs/config';
import {PassportStrategy} from '@nestjs/passport';
import {InjectRepository} from '@nestjs/typeorm';
import {ExtractJwt, Strategy} from 'passport-jwt';
import {Repository} from 'typeorm';
import {User} from '../esquemas/user.entity';
import {JwtPayload} from '../types/jwt-payload.type';

@Injectable()
export class JwtStrategy extends PassportStrategy(Strategy) {
	constructor(
		configService: ConfigService,
		@InjectRepository(User) private readonly usersRepository: Repository<User>,
	) {
		super({
			jwtFromRequest: ExtractJwt.fromAuthHeaderAsBearerToken(),
			ignoreExpiration: false,
			secretOrKey: configService.get<string>('JWT_SECRET', 'change-me'),
		});
	}

	async validate(payload: JwtPayload): Promise<User> {
		const user = await this.usersRepository.findOne({where: {id: payload.sub}});
		if (!user) {
			throw new UnauthorizedException('User not found');
		}
		return user;
	}
}

import {Controller, Get, Post, Put, Delete, Param, Body, Patch} from "@nestjs/common";
import {ApiTags, ApiOperation, ApiResponse} from '@nestjs/swagger';
import {ClientesService} from "./clientes.service";
import {CreateClientesDto} from "./esquemas/clientes.dto";
import {Public} from '../auth/decorators/public.decorator';

@Public()
@ApiTags('Clientes')
@Controller("clientes")
export class ClientesController {
	constructor(private readonly service: ClientesService) {}

	@Get()
	@ApiOperation({ summary: 'Obtener todos los clientes' })
	@ApiResponse({ status: 200, description: 'Lista de clientes.' })
	findAll() {
		return this.service.findAll();
	}

	@Get(":telefono")
	@ApiOperation({ summary: 'Obtener un cliente por tel├®fono' })
	@ApiResponse({ status: 200, description: 'Cliente encontrado.' })
	findOne(@Param("telefono") telefono: string) {
		return this.service.findOne(telefono);
	}

	@Post()
	@ApiOperation({ summary: 'Crear un cliente' })
	@ApiResponse({ status: 201, description: 'Cliente creado.' })
	create(@Body() dto: CreateClientesDto) {
		return this.service.create(dto);
	}

	@Patch(":telefono")
	@ApiOperation({ summary: 'Actualizar un cliente' })
	@ApiResponse({ status: 200, description: 'Cliente actualizado.' })
	update(@Param("telefono") telefono: string, @Body() dto: Partial<CreateClientesDto>) {
		return this.service.update(telefono, dto);
	}

	@Delete(":telefono")
	@ApiOperation({ summary: 'Eliminar un cliente' })
	@ApiResponse({ status: 200, description: 'Cliente eliminado.' })
	remove(@Param("telefono") telefono: string) {
		return this.service.remove(telefono);
	}
}

import {Injectable} from "@nestjs/common";
import {InjectRepository} from "@nestjs/typeorm";
import {Repository} from "typeorm";
import {Clientes} from "./esquemas/clientes.entity";
import {CreateClientesDto} from "./esquemas/clientes.dto";

@Injectable()
export class ClientesService {
	constructor(
		@InjectRepository(Clientes)
		private readonly repo: Repository<Clientes>,
	) {}

	findAll(page = 1, limit = 500) {
		return this.repo.find({
			take: limit,
			skip: (page - 1) * limit,
			relations: ['domicilios']
		});
	}

	findOne(telefono: string) {
		return this.repo.findOne({
			where: { telefono },
			relations: ['domicilios']
		});
	}

	create(data: CreateClientesDto) {
		return this.repo.save(data);
	}

	update(telefono: string, data: Partial<CreateClientesDto>) {
		return this.repo.update(telefono, data);
	}

	remove(telefono: string) {
		return this.repo.delete(telefono);
	}
}

