		const end = new Date();
		end.setHours(23, 59, 59, 999);
		return this.repo
			.createQueryBuilder('f')
			.leftJoinAndSelect('f.ordenes', 'ordenes')
			.leftJoinAndSelect('f.domicilios', 'domicilios')
			.where('(f.estado = :pendiente OR f.estado IS NULL)')
			.andWhere('f.fechaFactura BETWEEN :start AND :end', {start, end, pendiente: 'pendiente'})
			.getMany();
	}

	async getDayStats() {
		const start = new Date();
		start.setHours(0, 0, 0, 0);
		const end = new Date();
		end.setHours(23, 59, 59, 999);

		const facturas = await this.repo
			.createQueryBuilder('f')
			.where('f.fechaFactura BETWEEN :start AND :end', {start, end})
			.andWhere('f.estado != :cancelado', {cancelado: 'cancelado'})
			.getMany();

		const totalDia = facturas.reduce((sum, f) => sum + (Number(f.total) || 0), 0);
		const totalPagado = facturas.filter((f) => f.estado === 'pagado').reduce((sum, f) => sum + (Number(f.total) || 0), 0);
		const totalPendiente = facturas
			.filter((f) => f.estado !== 'pagado')
			.reduce((sum, f) => sum + (Number(f.total) || 0), 0);

		return {
			totalDia,
			totalPagado,
			totalPendiente,
			count: facturas.length,
		};
	}

	findOne(id: number) {
		return this.repo.findOne({
			where: {facturaId: id},
			relations: ['ordenes', 'domicilios'],
		});
	}

	create(data: CreateFacturasVentasDto) {
		return this.repo.save(data);
	}

	update(id: number, data: Partial<CreateFacturasVentasDto>) {
		return this.repo.update(id, data);
	}

	remove(id: number) {
		return this.repo.delete(id);
	}
}

import * as dotenv from 'dotenv';
dotenv.config();

import {NestFactory} from '@nestjs/core';
import {ValidationPipe} from '@nestjs/common';
import {AppModule} from './app.module';
import {DocumentBuilder, SwaggerModule} from '@nestjs/swagger';
import {LoggingInterceptor} from './common/interceptors/logging.interceptor';
import {RedisIoAdapter} from './common/redis/redis.adapter';

async function bootstrap() {
	const app = await NestFactory.create(AppModule);

	// Global request/response logging
	app.useGlobalInterceptors(new LoggingInterceptor());

	// Enforce DTO validation/transformation
	app.useGlobalPipes(
		new ValidationPipe({
			whitelist: true,
			transform: true,
			transformOptions: {enableImplicitConversion: true},
		}),
	);

	const config = new DocumentBuilder()
		.setTitle('DfiruBack API')
		.setDescription('Documentaci├│n Swagger de la API DfiruBack')
		.setVersion('1.0')
		.addBearerAuth()
		.build();
	const document = SwaggerModule.createDocument(app, config);
	SwaggerModule.setup('swagger', app, document, {
		swaggerOptions: {persistAuthorization: true},
	});

	// Habilitar CORS con lista de or├¡genes permitidos (CORS_ORIGINS separado por comas)
	const allowedOrigins = process.env.CORS_ORIGINS
		?.split(',')
		.map((origin) => origin.trim())
		.filter(Boolean);

	app.enableCors({
		origin: allowedOrigins && allowedOrigins.length > 0 ? allowedOrigins : ['tauri://localhost', 'http://localhost:1420', 'http://localhost:8081', '*'],
		credentials: true,
	});

	// Configurar Redis IO Adapter
	const redisIoAdapter = new RedisIoAdapter(app);
	await redisIoAdapter.connectToRedis();
	app.useWebSocketAdapter(redisIoAdapter);

	const port = Number(process.env.PORT) || 3000;
	// HOST puede ser una IP espec├¡fica (ej: 192.168.1.50) o 0.0.0.0 para todas las interfaces
	// IP por defecto: 0.0.0.0 (todas las interfaces). Evitar IP inv├ílida hardcodeada.
	const host = process.env.HOST || '0.0.0.0';
	await app.listen(port, host);
	console.log(`Servidor escuchando en http://${host}:${port}`);
}
bootstrap().catch((err) => {
	console.error('Error al iniciar la aplicaci├│n', err);
	process.exit(1);
});

